/* HELPER TYPES */
const METHODS = [
    'ACL',
    'BIND',
    'CHECKOUT',
    'CONNECT',
    'COPY',
    'DELETE',
    'GET',
    'HEAD',
    'LINK',
    'LOCK',
    'M-SEARCH',
    'MERGE',
    'MKACTIVITY',
    'MKCALENDAR',
    'MKCOL',
    'MOVE',
    'NOTIFY',
    'OPTIONS',
    'PATCH',
    'POST',
    'PRI',
    'PROPFIND',
    'PROPPATCH',
    'PURGE',
    'PUT',
    'REBIND',
    'REPORT',
    'SEARCH',
    'SOURCE',
    'SUBSCRIBE',
    'TRACE',
    'UNBIND',
    'UNLINK',
    'UNLOCK',
    'UNSUBSCRIBE'
];
/** HELPER METHODS */
const createMiddlewareFromRoute = ({ path, handler, method }) => ({
    method,
    handler: handler || path,
    path: typeof path === 'string' ? path : '/'
});
/**
 * Push wares to a middleware array
 * @param mw Middleware arrays
 */
const pushMiddleware = (mw) => ({ path, handler, method, handlers, type }) => {
    const m = createMiddlewareFromRoute({ path, handler, method, type });
    let waresFromHandlers = [];
    if (handlers) {
        waresFromHandlers = handlers.flat().map((handler) => createMiddlewareFromRoute({
            path,
            handler,
            method,
            type
        }));
    }
    for (const mdw of [m, ...waresFromHandlers])
        mw.push({ ...mdw, type });
};
/**
 * tinyhttp Router. Manages middleware and has HTTP methods aliases, e.g. `app.get`, `app.put`
 */
class Router {
    constructor() {
        this.middleware = [];
        this.mountpath = '/';
        this.apps = {};
        for (const m of METHODS) {
            this[m.toLowerCase()] = this.add(m);
        }
    }
    add(method) {
        return (...args) => {
            const handlers = args.slice(1).flat();
            pushMiddleware(this.middleware)({
                path: args[0],
                handler: handlers[0],
                handlers: handlers.slice(1),
                method,
                type: 'route'
            });
            return this;
        };
    }
    msearch(...args) {
        const handlers = args.slice(1).flat();
        pushMiddleware(this.middleware)({
            path: args[0],
            handler: handlers[0],
            handlers: handlers.slice(1),
            method: 'M-SEARCH',
            type: 'route'
        });
        return this;
    }
    all(...args) {
        const handlers = args.slice(1).flat();
        pushMiddleware(this.middleware)({
            path: args[0],
            handler: handlers[0],
            handlers: handlers.slice(1),
            type: 'route'
        });
        return this;
    }
    /**
     * Return the app's absolute pathname
     * based on the parent(s) that have
     * mounted it.
     *
     * For example if the application was
     * mounted as `"/admin"`, which itself
     * was mounted as `"/blog"` then the
     * return value would be `"/blog/admin"`.
     *
     */
    path() {
        return this.parent ? this.parent.path() + this.mountpath : '';
    }
    /**
     * Push middleware to the stack
     */
    use(...args) {
        const base = args[0];
        const handlers = args.slice(1).flat();
        if (typeof base === 'string') {
            pushMiddleware(this.middleware)({
                path: base,
                handler: handlers[0],
                handlers: handlers.slice(1),
                type: 'mw'
            });
        }
        else {
            pushMiddleware(this.middleware)({
                path: '/',
                handler: Array.isArray(base) ? base[0] : base,
                handlers: Array.isArray(base)
                    ? [...base.slice(1), ...handlers]
                    : handlers,
                type: 'mw'
            });
        }
        return this;
    }
}

export { Router, pushMiddleware };
